mapper.select:
SELECT * FROM user_original_info ,user_dynamic_info

    id = #{id}
    AND username = #{username}
    and user_id = user_original_info.id

mapper.update:
UPDATE user_original_info
username = #{username}, password = #{password}, security_question = #{securityQuestion}, account_status = #{accountStatus}, create_date = #{createDate}, roles = #{roles}, email = #{email}, phone = #{phone}, gender = #{gender}, country = #{country}, data_of_birth = #{dataOfBirth}, social_media_links = #{socialMediaLinks}, profile_picture = #{profilePicture, jdbcType=BLOB},
WHERE id = #{id}
